<script lang="ts" setup>
    import {onMounted, ref} from 'vue'
    import {useAreaBrodmannStore} from '@/store/index'
    
    const store = useAreaBrodmannStore();
    const isActive = ref(false)
    const areaRefActive = ref(0)
    onMounted(()=>{
        // console.log(store.areaSelected);
        
        // console.log(clasesArray);
        store.$subscribe((mutation, state)=>{
            // console.log("state suscribed: ",state.areaSelected)
            const areaRef = ref("area"+String(state.areaSelected));
            // console.log("llega areaRef",areaRef.value)
            isActive.value = state.isActiveArea;
            areaRefActive.value = state.areaSelected
            // console.log("areaRefActive.value",areaRefActive.value)
        })
    })
</script>
<style scoped>
.brodmann-area{
    opacity: 0;
    transform-origin: 50% 50%;
    transition: all 0.3s;
}
.active{
    opacity: 1;
    filter: drop-shadow(4px 4px 15px #000000);
    stroke-width:3;
    transform: scale(1.02,1.02);
    z-index: 1;
}
</style>